            if (action == 'add') {
                if (type == 'name'){
                    $scope.selectedData.push(data)
                }
                if (type == 'inip'){
                    $scope.selectedData.push(data)
                }
                if (type == 'exip'){
                    $scope.selectedData.push(data)
                }
            }
            if (action == 'remove'){
                if (type == 'name'){
                    $scope.selectedData.splice($scope.selectedData.indexOf(data), 1)
                }
                if (type == 'inip'){
                    $scope.selectedData.splice($scope.selectedData.indexOf(data), 1)
                }
                if (type == 'exip'){
                    $scope.selectedData.splice($scope.selectedData.indexOf(data), 1)
                }
            }
